int p10(int n) {
    int ret = 1;
    for (int i = 0; i < n; i++) {
        ret *= 10;
    }
    return ret;
}

int main(void) {
    int target, num;
    while (cin >> target >> num) {
        if (target == 0 && num == 0) break;
        int keta = 0, tmp = num;
        while (tmp) {
            keta++;
            tmp /= 10;
        }
        int dest = -INF;
        for (int digit = 0; digit < (1<<(keta-1)); digit++) {
//            cout << digit << endl;
            int sum = 0, old = -1;
            for (int i = 0; i < digit; i++) {
                if (((digit >> i) & 1) == 1) {
                    sum += (num % p10(i+1)) / p10(old+1);
                    old = i;
//                    printf("+%d\n", (num % p10(i+1) / p10(old+1)));
                }
            }
            sum += (num % p10(keta)) / p10(old+1);
//            printf("+%d\n", (num % p10(keta) / p10(old+1)));
            if (sum <= target) {
                dest = max(sum, dest);
            }
        }
        if (dest == -INF) cout << "error" << endl;

        else {
            int cnt = 0;
            int index = 0;
            bool flag = true;
            for (int digit = 0; digit < (1<<(keta-1)); digit++) {
                int sum = 0, old = -1;
                for (int i = 0; i < keta; i++) {
                    if (((digit >> i) & 1) == 1) {
                        sum += (num % p10(i+1)) / p10(old+1);
                        old = i;
                    }
                }
                sum += (num % p10(keta)) / p10(old+1);
                if (sum == dest) {
                    cnt++;
                    if (cnt > 1) {
                        cout << "rejected" << endl;
                        flag = false;
                        break;
                    }
                    index = digit;
                }
            }
            if (flag) {
                vector<int> ans;
                cout << dest;
                int old = -1;
                for (int i = 0; i < keta; i++) {
                    if (((index >> i) & 1) == 1) {
                        ans.push_back((num % p10(i+1)) / p10(old+1));
                        old = i;
                    }
                }
                ans.push_back((num % p10(keta)) / p10(old+1));
                reverse(ans.begin(), ans.end());
                for (int i = 0; i < ans.size(); i++) {
                    cout << " " << ans[i];
                }
                cout << endl;
            }
        }
    }
    return 0;
}

