void solve(vector<int>& seq, int idx = 0) {
	if(idx == (int)num.size()) {
		int tmp = 0;
		for(int i = 0; i < (int)seq.size(); ++i)
			tmp += seq[i];

		if(tmp <= target) {
			if(tmp > sum) {
				rejected = false;
				sum = tmp;
				part = seq;
			}
			else if(tmp == sum) {
				rejected = true;
			}
		}
		return;
	}

	seq.push_back(num[idx] - '0');
	solve(seq, idx + 1);
	seq.pop_back();

	for(int i = idx + 1; i < (int)num.size(); ++i) {
		seq.push_back(stoi(num.substr(idx, i - idx + 1)));
		solve(seq, i + 1);
		seq.pop_back();
	}
}

