vector<int> calc(int t, string &num, int key){
	vector<int> sum;
	string::reverse_iterator it = num.rbegin();
	int temp = *it - '0';
	int mlt = 10;
	it++;
	for(int i = 1; i <= 1 << (num.size() -2); i <<= 1){
		if((key & i) == i){
			sum.push_back(temp);
			temp = *it - '0';
			mlt = 10;
		}
		else{
			temp += (*it - '0')*mlt;
			mlt *= 10;
		}
		it++;
	}
	sum.push_back(temp);
	/*
	for(int i = 0; i < sum.size(); i++){
