#include <iostream>
#include <vector>
using namespace std;

vector<int> solve(int t, string num, vector<int> ret, int i, int k){
  if(i == -1) return ret;
  bool f = false, g = false;
  vector<int> r1, r2;
  if(ret[0] != -1 && ret[0] + k*10*(num[i]-'0') <= t){
    ret[ret.size()-1] += k*10*(num[i]-'0');
    ret[0] += k*10*(num[i]-'0');
    r1 = solve(t,num,ret,i-1,k*10);
    ret[ret.size()-1] -= k*10*(num[i]-'0');
    ret[0] -= k*10*(num[i]-'0');
    f = (r1[0] != -1);
  }
  if(ret[0] != -1 && ret[0] + (num[i]-'0') <= t){
    ret.push_back(num[i]-'0');
    ret[0] += (num[i]-'0');
    r2 = solve(t,num,ret,i-1,1);
    ret.pop_back();
    ret[0] -= (num[i]-'0');
    g = (r2[0] != -1);
  }
  if(f && g){
    if(r1[0] == r2[0]){
      vector<int> r(2,-1);
      r[0] = r1[0];
      return (r);
    }else{
      return max(r1,r2);
    }
  }
  if(f) return r1;
  if(g) return r2;
  vector<int> r(1,-1);
  return r;
}

