  int t;
  string num;
  while(cin >> t >> num, t){
    int n = num.length();
    vector<int> init(1,num[n-1]-'0');
    init.push_back(num[n-1]-'0');
    vector<int> ans = solve(t,num,init,n-2,1);
    if(ans[0] == -1) cout << "error" << endl;
    else if(ans[1] == -1) cout << "rejected" << endl;
    else{
      cout << ans[0];
      for(int i = ans.size()-1; i > 0; --i)
        cout << " " << ans[i];
      cout << endl;
    }
  }
  return 0;
}

