void f(int i, string S, vector<int> pre, map<int, int> &m) {

    if (i == S.size()) {
        int sum = accumulate(pre.begin(), pre.end(), 0);
        m[sum]++;
        return;
    }

    int d = 0;
    for (int j = i; j < S.size(); j++) {
        d = d * 10 + (S[j] - '0');
        auto D = pre;
        D.push_back(d);
        f(j + 1, S, D, m);
    }
}

void g(int i, string S, vector<int> pre, int aim) {
    if (i == S.size()) {
        int sum = accumulate(pre.begin(), pre.end(), 0);
        if (sum == aim) {
            cout << sum << " ";
            for (int j = 0; j < pre.size(); j++) {
                if (j != pre.size() - 1) {
                    cout << pre[j] << " ";
                } else {
                    cout << pre[j] << endl;
                }
            }
        }
        return;
    }

    int d = 0;
    for (int j = i; j < S.size(); j++) {
        d = d * 10 + (S[j] - '0');
        auto D = pre;
        D.push_back(d);
        g(j + 1, S, D, aim);
    }
}

int main() {

    while (true) {

        int t, num;
        cin >> t >> num;

        if (t == 0 && num == 0) break;

        if (t == num) {
            cout << t << " " << num << endl;
            continue;
        }

        map<int, int> m;
        string S = to_string(num);
        f(0, S, vector<int>(), m);

        int maxV = -1;
        for (auto x: m) if (x.first <= t) maxV = max(x.first, maxV);

        if (maxV == -1) {
            cout << "error" << endl;
            continue;
        }

        bool rejected = false;
        for (auto x: m) if (x.first == maxV && x.second > 1) rejected = true;
        if (rejected) {
            cout << "rejected" << endl;
            continue;
        }

        g(0, S, vector<int>(), maxV);

    }

    return 0;
}

