map<int, pair<vector<int>,int>>getans(string st) {
	map<int, pair<vector<int>,int>>ans;
	ans[stoi(st)] = make_pair(vector<int>(1,stoi(st)),1);
	if (st.size()) {
		for (int i = 1; i < st.size(); ++i) {
			map<int, pair<vector<int>, int>>mp(getans(st.substr(i, st.size() - i)));
			for (auto& j : mp) {
				string from = st.substr(0, i);
				vector<int>now(1, stoi(from) );
				if (!ans.count(stoi(from) + j.first)) {		
					now.insert(now.end(), j.second.first.begin(), j.second.first.end());
					ans[stoi(from) + j.first] = make_pair(now, j.second.second);
				}
				else {
					ans[stoi(from) + j.first].second+=j.second.second;
				}
			}
		}
	}

	return ans;
}

int main() {
	while (1) {
		int sum; string num; cin >> sum >> num;
		if (sum == 0)break;
		map<int, pair<vector<int>, int>>mp(getans(num));
		auto it(mp.upper_bound(sum));

		if (it == mp.begin()) {
			cout << "error" << endl;
		}
		else if (prev(it)->second.second != 1) {
			cout << "rejected" << endl;
		}
		else {
			cout << prev(it)->first;
			for (int i = 0; i < prev(it)->second.first.size(); ++i) {
				cout <<" "<< prev(it)->second.first[i];
			}
			cout << endl;
		}

	}
	return 0;
}

