#define REP(i, n) for ( int i = 0; i < n; i++ )
#define NIL -1

using namespace std;

int N, maxVal;
vector<vector<int> > ans;

int getFigure(int num) {
    int ret = 0;
    while(num > 0) {
        num /= 10;
        ret++;
    }
    return ret;
}

void merge(vector<int> &nums, int idx) {
    int nonZero = 0,
        prevIdx = 0;
    REP(i, nums.size()) {
        if(nums[i] == NIL) continue;
        nonZero++;

        if(nonZero == idx + 2) {
            int step = getFigure(nums[i]);
            REP(j, step) nums[prevIdx] *= 10;
            nums[prevIdx] += nums[i];
            nums[i] = NIL;
            break;
        }
        prevIdx = i;
    }
}

int sum(vector<int> &nums) {
    int ret = 0;
    REP(i, nums.size()) if(nums[i]!=NIL) ret += nums[i];
    return ret;
}

void dump(vector<int> &nums) {
    REP(i, nums.size()) cout << nums[i] << ",";
    // REP(i, nums.size()) if(nums[i]!=NIL)cout << nums[i] << ",";
    cout << endl;
}

bool equal(vector<int> &a, vector<int> &b) {
    int ai = 0, bi = 0;
    REP(i, a.size()) {
        while(a[ai] == NIL) ai++;
        while(b[bi] == NIL) bi++;

        // cout << "ai:" << ai << ", bi:" << bi << endl;
        if(a[ai] != b[bi]) return 0;

        i = ai + 1;
        ai++; bi++;
    }
    return 1;
}

void solve(vector<int> &nums, int d = 0) {
    int s = sum(nums);

    if(d > nums.size()) return;
    if(s > N) return;

    if(s == maxVal && !equal(ans[0], nums)) {
        // cout << "最大値更新:" << maxVal << ", " << equal(ans[0], nums);
        // dump(ans[0]);
        // cout << endl;
        ans.push_back(nums);
    } else if (s > maxVal) {
        ans.clear();
        ans.push_back(nums);
        maxVal = s;
    }

    vector<int> base = nums;
    REP(i, nums.size() - (d+1)) {
        // cout << "hoge" << endl;
        merge(nums, i);
        solve(nums, d+1);
        nums = base;
    }
}

int main() {
    int num;
    vector<int> nums;

    while(cin >> N >> num, N||num) {

        maxVal = -1;
        ans.clear();
        nums.clear();

        while(num > 0) {
            nums.push_back(num % 10);
            num /= 10;
        }
        reverse(nums.begin(), nums.end());

        // dump(nums);
        // cout << N << ", " << num << ", " << ans.size() << ", " << maxVal << endl;

        solve(nums);

        if(ans.size() == 0) {
            cout << "error" << endl;
        } else if (ans.size() >= 2) {
            cout << "rejected" << endl;
        } else {
            cout << sum(ans[0]);
            REP(i, ans[0].size()) if(ans[0][i] != NIL) cout << " " << ans[0][i];
            cout << endl;
        }
    }

    return 0;
}

