int stoa(string s) {
	int ret = 0, t = 1;
	for(int i = s.size()-1; i >= 0; i--) {
		ret += (s[i] - '0') * t;
		t *= 10;
	}
	return ret;
}

int n;
string s;
int vmin = INF, vmax = 0, ans = 0, cnt = 0;
vector<int> res;

void dfs(int i, int sum, vector<int> v) {
	if(i == s.size()) {
		vmin = min(vmin, sum);
		vmax = max(vmax, sum);

		if(sum <= n) {
			if(ans < sum) {
				ans = sum;
				cnt = 1;
				res.clear();
				rep(i, v.size()) res.push_back(v[i]);
			} else if(ans == sum) {
				cnt++;
			}
		}
		return;
	}

	REP(j, 1, s.size() + 1) {
		// cout << "i:" << i <<  " j:" << j << " sum:" << sum << " " << s.substr(i, j) << " " << stoa(s.substr(i, j)) << endl;
		if(i + j <= s.size() ) {
			vector<int> t(v.begin(), v.end());
			t.push_back(stoa(s.substr(i, j)));
			dfs(i + j, sum + stoa(s.substr(i, j)), t);
		} else return;
	}
}

int main() {
	while(cin >> n >> s) {
		if(n == 0 && s == "0") break;

		vmin = INF, vmax = 0, ans = 0, cnt = 0;
		res.clear();

		vector<int> v;
		dfs(0, 0, v);

		if(vmin > n) {
			cout << "error" << endl;
		} else if(cnt >= 2) {
			cout << "rejected" << endl; 
		} else {
			cout << ans;
			rep(i, res.size()) {
				cout << " " << res[i];
			}
			cout << endl;
		}
	}
	return 0;
}

