int s2i(string s){
    stringstream ss;
    ss << s;
    int ret;
    ss >> ret;
    return ret;
}
int main(){
    while(true){
        string target,input;
        cin >> target >> input;
        if(target == "0") break;
        int itarget = s2i(target);

        if(input.length() == 1){
            int iinput = s2i(input);
            if(iinput <= itarget){
                cout << iinput << " " << iinput << endl;
            }else{
                cout << "error" << endl;
            }
        }else{
            int N = input.size()-1;

            int maxi = -1;
            bool reject = false;
            vector<string> ans;
            for(int i=0;i<(1 << N);i++){
                vector<string> cutup;
                int before=0;
                for(int j=0;j<N;j++){
                    if(i & (1 << j)){
                        cutup.push_back(input.substr(before,j-before+1));
                        before = j+1;
                    }
                }
                cutup.push_back(input.substr(before));
                // for(int j=0;j<cutup.size();j++){
                //     cerr << cutup[j] << " ";
                // }
                // cerr << endl;
                int nyan = 0;
                for(int i=0;i<cutup.size();i++){
                    nyan += s2i(cutup[i]);
                }
                if(nyan > itarget) continue;
                if(maxi == nyan){
                    reject = true;
                }else if(nyan > maxi){
                    maxi = nyan;
                    ans = cutup;
                    reject = false;
                }
            }
            if(reject){
                cout << "rejected" << endl;
            }else if(maxi == -1){
                cout << "error" << endl;
            }else{
                cout << maxi << " ";
                for(int i=0;i<ans.size();i++){
                    cout << " " << ans[i];
                }
                cout << endl;
            }
        }
    }
    return 0;
}

