}

int main(){
  cin.tie(0);
  ios_base::sync_with_stdio(false);

  int T, N;
  while(cin>>T>>N,T){
	int dig = log10(N);
	int ans = -1;
	map<int,int> mp;
	VI part;
	for(int bit=0;bit<(1<<dig);++bit){
	  VI tmp;
	  int num = N, sum = 0, prv = 0;
	  for(int i=0;i<dig;++i){
		if((bit>>i)&1){
		  int x = (num % pow(10,i+1)) / pow(10,prv);
		  sum += x;
		  prv = i+1;
		  num = num / pow(10,i+1) * pow(10,i+1);
		  tmp.PB(x);
		}
	  }
	  if(num > 0){
		sum += num / pow(10,prv);
		tmp.PB(num/pow(10,prv));
	  }

	  if(sum <= T && ans <= sum){
		  ans = sum, part = tmp;
		  mp[ans]++;
	  }
	}
	if(mp[ans] > 1) cout << "rejected" << endl;
	else if(ans == -1) cout << "error" << endl;
	else{
	  cout << ans;
	  for(int i=SZ(part)-1;i>=0;--i)
		cout << " " << part[i];
	  cout << endl;
	}
  }
  return 0;
}

