using namespace std;

int N, maxVal = -1;
vector<vector<int> > ans;

int getFigure(int num) {
    int ret = 0;
    while(num > 0) {
        num /= 10;
        ret++;
    }
    return ret;
}

void merge(vector<int> &nums, int idx) {
    int nonZero = 0,
        prevIdx = 0;
    REP(i, nums.size()) {
        if(nums[i] == NIL) continue;
        nonZero++;

        if(nonZero == idx + 2) {
            int step = getFigure(nums[i]);
            REP(j, step) nums[prevIdx] *= 10;
            nums[prevIdx] += nums[i];
            nums[i] = NIL;
            break;
        }
        prevIdx = i;
    }
}

void toEmpty(vector<vector<int> > &vec) {
    REP(i, vec.size()) vec.clear();
}

int sum(vector<int> &nums) {
    int ret = 0;
    REP(i, nums.size()) if(nums[i]!=NIL) ret += nums[i];
    return ret;
}

void dump(vector<int> &nums) {
    REP(i, nums.size()) cout << nums[i] << ",";
    // REP(i, nums.size()) if(nums[i]!=NIL)cout << nums[i] << ",";
    cout << endl;
}

bool equal(vector<int> &a, vector<int> &b) {
    int ai = 0, bi = 0;
    REP(i, a.size()) {
        while(a[ai] == NIL) ai++;
        while(b[bi] == NIL) bi++;

        // cout << "ai:" << ai << ", bi:" << bi << endl;
        if(a[ai] != b[bi]) return 0;

        i = ai + 1;
        ai++; bi++;
    }
    return 1;
}

void solve(vector<int> &nums, int d = 0) {
    // cout << "depth:" << d << endl;
    // dump(nums);

    vector<int> base = nums;
    int s = sum(nums);

    if(d > nums.size()) return;
    if(s > N) return;

    if(s == maxVal && !equal(ans[0], nums)) {
        ans.push_back(nums);
    } else if (s > maxVal) {
        ans.clear();
        ans.push_back(nums);
        maxVal = s;
    }

    REP(i, nums.size() - (d+1)) {
        merge(nums, i);
        s = sum(nums);

        if(s > N) {
            nums = base;
            continue;      // TODO: 以上？未満？
        }

        solve(nums, d+1);
        nums = base;
    }
}

int main() {
    int num;

    while(cin >> N >> num, N||num) {
        vector<int> nums;

        maxVal = -1;
        ans.clear();

        while(num > 0) {
            nums.push_back(num % 10);
            num /= 10;
        }
        reverse(nums.begin(), nums.end());

        // cout << N << "," << num << "," << ans.size() << "," << maxVal << endl;

        solve(nums);

        if(ans.size() == 0) {
            cout << "error" << endl;
        } else if (ans.size() >= 2) {
            cout << "rejected" << endl;
        } else {
            cout << sum(ans[0]);
            REP(i, ans[0].size()) if(ans[0][i] != NIL) cout << " " << ans[0][i];
            cout << endl;
        }
    }

    return 0;
}

