typedef pair<int, vector<int> > Paper;
Paper solve(int t, int num){
    string s = to_string(num);
    int L = (int)s.size() - 1;
    vector<Paper> cands;
    rep(i, 1<<L){
        vector<int> parts;
        for(int j=0; j<=L; ++j){
            string d = "";
            for(d+=s[j]; j<=L; ++j, d+=s[j])if(i >> j & 1)break;
            parts.push_back(stoi(d));
        }
        cands.emplace_back(accumulate(all(parts), 0), parts);
    }
    sort(all(cands));
    int k = count_if(all(cands), [&](const Paper& p){return p.first <= t;});
    if(k == 0)throw "error";
    if(count_if(cands.begin(), cands.begin() + k - 1,
                [&](const Paper& p){return p.first == cands[k-1].first;}))throw "rejected";
    return cands[k-1];
}

int main(){
    int t, num;
    while(cin >> t >> num, t|num){
        try{
            Paper ans = solve(t, num);
            cout << ans.first;
            for(const auto e: ans.second)cout << " " << e;
            cout << '\n';
        }catch(const char* mes){
            cout << mes << '\n';
        }
    }
    return 0;
}

