int main()
{
	int t;
	string num;
	while(1)
	{
		cin>>t>>num;
		if(t==0 && num=="0")
			break;
		if(to_string(t)==num)
		{
			cout<<num<<" "<<num<<endl;
			continue;
		}
		int keta=num.size()-1;//仕切りの数を求める
		//cerr<<keta<<" "<<(1<<keta)<<endl;
		int sum[1<<6];//それぞれの和
		fill(sum,sum+(1<<6),1e8-1);
		int m=0;
		int mind=-1;
		for(int bit=0;bit<(1<<keta);bit++)
		{
			//bitが1のときnumの以下に対応するところで切る
			//num 1 2 3 4 5 6
			//bit  4 3 2 1 0
			sum[bit]=0;
			int tmp=0;
			int cnt=1;
			int last=-1;
			for(int i=0;i<keta+1;i++)
			{
				tmp+=(num[i]-'0');
				if(bit&(1<<i))
				{
					sum[bit]+=tmp;
					tmp=0;
					cnt=0;
					last=i;
				}
				tmp*=10;
				cnt++;
			}
			//cerr<<bitset<4>(bit)<<" "<<last<<" "<<cnt<<endl;
			sum[bit]+=(tmp/10);
			if(sum[bit]>t)
				sum[bit]=1e8-1;
			if(sum[bit]!=1e8-1 && sum[bit]>m)
			{
				m=sum[bit];
				mind=bit;
			}
		}
		//cerr<<mind<<" "<<m<<endl;
		if(mind==-1)
		{
			cout<<"error"<<endl;
			continue;
		}
		//重複がないか確かめる
		bool f=false;
		for(int i=0;i<(1<<6);i++)
		{
			if(sum[i]==m && mind!=i)
			{
				cout<<"rejected"<<endl;
				f=true;
				break;
			}
		}
		if(f)
			continue;
		//構築
		cout<<m<<" ";
		for(int i=0;i<keta+1;i++)
		{
			cout<<num[i];
			if(mind&(1<<i))
				cout<<" ";
		}
		cout<<endl;
	}
}

