inline int cut(string in,int mask){
	int res=0,cur=0;
	int n=in.size();
	rep(i,n){
		cur=cur*10+(in[i]-'0');
		if(mask&(1<<i))
			res+=cur,cur=0;
	}
	return res+cur;
