int calc(char *s, char *cut, int w)
{
	int i, k = 0, sum = 0;
	for (i = 0; i <= w; i++) {
		k = 10*k + (s[i] & 0xf);
		if (cut[i]) sum += k, k = 0;
	}
	return sum + k; 
}

