		w = strlen(s)-1, lim = 1 << w;
		for (max = 0, i = 0; i < lim; i++) {
			memset(cut, 0, sizeof(cut));
			for (j = 0; j <= w; j++) if ((i >> j) & 1) cut[j] = 1;
			k = calc(s, cut, w);
