			int t = sc.nextInt();
			String s = sc.next();
			if(t==0&&"0".equals(s))break;
			int max = -1;
			List<String> piece = new ArrayList<String>();
			boolean col = false;
			int n = s.length()-1;
			for(int i=0;i<1<<n;i++){
				int p = 0;
				int sum = 0;
				List<String> sub = new ArrayList<String>();
