public class Main {

	Scanner sc = new Scanner(in);

	void quickSort(int[] a, int[] b, int l, int r) {
		if (l < r) {
			int w2 = a[(l+r)/2];

			int i = l, j = r, temp, temp2;
			while (i < j) {
				while (a[i] > w2) i++;
				while (a[j] < w2) j--;
				if (i <= j) {
					temp = a[i];
					a[i] = a[j]; a[j] = temp;
					temp2 = b[i];
					b[i] = b[j]; b[j] = temp2;
					i++; j--;
				}
			}
			quickSort(a, b, l, j);
			quickSort(a, b, i, r);
		}
	}

	void run() {
		while (true) {
			int t = sc.nextInt();
			String number = sc.next();
			if (t == 0 && number.equals("0")) break;

