	static int len;
	static String[] ans;
	static int n;

	public static void main(String[]agrs){
		Scanner sc = new Scanner(System.in);
		while(true) {
			max = -1;
			Count = -1;
			len = -1;
			ans = new String[100];
			n = sc.nextInt();
			String in = sc.next();
			if(n == 0 && in.equals("0")) break;
			dfs(in,0,1,new String[in.length()+1],0,0);

			if(max == -1) System.out.println("error");
			else if(Count >= 2) System.out.println("rejected");
			else {
				System.out.print(max);
				for(int i = 0; i < len; i++) {
					System.out.print(" " + ans[i]);
				}
				System.out.println();
			}
		}
	}

	public static void dfs(String a,int l, int r, String[] b,int sum,int count) {
		if(l == a.length()) {
			if(max < sum && sum <= n) {
				max = sum;
				ans = b;
				Count = 1;
				len = count;
			}
			else if(sum == max) {
				Count++;
			}
			return ;
		}
		if(r == a.length()+1) {
			return;
		}

		dfs(a,l,r+1,b,sum,count);

		String[] cp = Arrays.copyOf(b, b.length);
		cp[count] = a.substring(l, r);


		sum += Integer.parseInt(a.substring(l, r));

		dfs(a,r,r+1,cp,sum,count+1);
	}
}

import java.util.Scanner;

public class Main {
	private static int N;

	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);
		while (true) {
			N = sc.nextInt();
			String M = sc.next();
			if (N == 0) {
				sc.close();
				break;
			}

			String log = DFS(M, 0, "");
			int ans = Integer.parseInt(log.split(" ")[0]);
			if (ans == -1) {
				System.out.println("error");
				continue;
			}

			String[] pieces = log.split(" ")[1].split(",");
			int testsum = 0;
			for (int i = 0; i < pieces.length; i++) {
				if (!pieces[i].equals("")) {
					testsum += Integer.parseInt(pieces[i]);
				}
			}
			if (testsum > ans) {
				System.out.println("rejected");
				continue;
			}

			System.out.print(ans);
			for (int i = 0; i < pieces.length; i++) {
				if (!pieces[i].equals("")) {
					System.out.print(" " + pieces[i]);
				}
			}
			System.out.println();

		}

	}

	static String DFS(String rest, int sum, String log) {
		int ans = -1;
		String logger = "";
		for (int i = 1; i <= rest.length(); i++) {
			int p = Integer.parseInt(rest.substring(0, i));
			if (i == rest.length()) {
				if (sum + p <= N) {
					if (sum + p > ans) {
						ans = sum + p;
						logger = log + "," + rest.substring(0, i);
					} else if (sum + p == ans) {
						logger = logger + "," + log + ","
								+ rest.substring(0, i);
					}
				}
			} else if (sum + p <= N) {
				String t = DFS(rest.substring(i), sum + p,
						log + "," + rest.substring(0, i));
				int tmp = Integer.parseInt(t.split(" ")[0]);
				if (tmp <= N && tmp > 0) {
					if (ans < tmp) {
						ans = tmp;
						logger = t.split(" ")[1];
					} else if (ans == tmp) {
						logger = logger + "," + t.split(" ")[1];
					}
				}
			}

		}

		return String.valueOf(ans) + " " + logger;

	}
}

import java.util.Arrays;
import java.util.Scanner;

public class Main {
	int n, target;
	int ans = -1;
	boolean rejected = true;
	int nums[];
	int temp[] = new int[6];

	public void shred(int x, int num, int index) {
		for (int i = 10; i <= (num * 10); i *= 10) {
			int mod = num % i;
			temp[index] = mod;
			if (x + mod > n) break;
			//System.out.println((x + mod) + " " + num);
			if (x + mod > ans && num / i == 0) {
				rejected = false;
				ans = x + mod;
				nums = Arrays.copyOf(temp, temp.length);
			} else if (x + mod == ans) {
				rejected = true;
			} else {
				shred(x + mod, num / i, index + 1);
			}
			temp[index] = -1;
		}
	}

	public void run() {
		Scanner in = new Scanner(System.in);
		while (true) {
			ans = -1;
			Arrays.fill(temp, -1);
			n = in.nextInt();
			target = in.nextInt();
			if ((n|target) == 0) break;
			shred(0, target, 0);
			if (ans == -1) System.out.println("error");
			else if (rejected) System.out.println("rejected");
			else {
				System.out.print(ans);
				for (int i = nums.length - 1; i >= 0; i--) {
					if (nums[i] == -1) continue;
					System.out.print(" " + nums[i]);
				}
				System.out.println();
			}
		}
	}
	public static void main(String[] args) {
		new Main().run();
	}

}

