using System.Linq;
using System.Collections.Generic;
using System;

public class P
{
    public string cut { get; set; }
    public int p { get; set; }
    public int sum { get; set; }
}

public class hello
{
    public static void Main()
    {
        while (true)
        {
            string[] line = Console.ReadLine().Trim().Split(' ');
            var t = line[0];
            var s = line[1];
            if (t == "0" && s == "0") break;
            getAns(t, s);
        }
    }
    static bool check(string t, string s)
    {
        if (t == s) { Console.WriteLine("{0} {1}", t, s); return false; }
        var sum = 0;
        for (int i = 0; i < s.Length; i++)
            sum += s[i] - '0';
        if (sum > int.Parse(t)) { Console.WriteLine("error"); return false; }
        else return true;
    }
    static void getAns(string t, string s)
    {
        if (!check(t, s)) return;
        var ta = int.Parse(t);
        var sL = s.Length;
        var q = new Queue<P>();
        for (int i = 1; i <= sL; i++)
        {
            var w = s.Substring(0, i);
            var ip = int.Parse(w);
            if (ip <= ta) q.Enqueue(new P { cut = i.ToString(), p = i, sum = ip });
            else break;
        }
        var ans = 0;
        var ansp = new List<P>();
        while (q.Count() > 0)
        {
            var a = q.Dequeue();
            if (a.p >= sL)
            {
                if (a.sum == ans)
                {
                    ansp.Add(a);
                }
                else if (a.sum > ans)
                {
                    ansp.Clear();
                    ansp.Add(a);
                    ans = a.sum;
                }
            }
            for (int i = 1; i <= sL - a.p; i++)
            {
                var w = s.Substring(a.p, i);
                var ip = int.Parse(w);
                if (a.sum + ip <= ta) q.Enqueue(new P { cut = a.cut + i.ToString(), p = a.p + i, sum = a.sum + ip });
                else break;
            }
        }
        if (ansp.Count() > 1) Console.WriteLine("rejected");
        else print(ansp[0], s);
    }
    static void print(P a, string s)
    {
        var cut = new List<int>() { a.sum };
        var sp = 0;
        var cp = 0;
        while (cp < a.cut.Length)
        {
            var w = a.cut[cp++] - '0';
            var w2 = s.Substring(sp, w);
            sp += w;
            cut.Add(int.Parse(w2));
        }
        Console.WriteLine(string.Join(" ", cut));
    }
}

