void main() {
    int T, N;
    bool input() {
        scanf("%d %d\n", &T, &N);
        return (T != 0 && N != 0);
    }
    void solve() {
        string S = N.to!string;
        int X = cast(int)(S.length);
        int Ans = -1;
        int[] Pat;
        bool Reject;
        void dfs(int index, int[] r) {
            if (index == X) {
                //writeln(r);
                int x = r.reduce!"a + b";
                if (x > T) return;
                if (x > Ans) {
                    Ans = x;
                    Pat = r;
                    Reject = false;
                } else if (x == Ans) {
                    Reject = true;
                }
            }
            for (int i = index + 1; i <= X; i++) {
                string s = S[index .. i];
                //if (s.length > 1 && s[0] == '0') continue;
                dfs(i, r ~ s.to!int);
            }
        }
        dfs(0, []);
        if (Reject) {
            writeln("rejected");
        } else if (Ans < 0) {
            writeln("error");
        } else {
            writefln("%d %(%s %)", Ans, Pat);
        }
    }
    while (input) solve;
}

def shred(num, bit):
    num = list(str(num))
    i = 0
    while bit:
        if bit & 1:
            num[i] += num.pop(i+1)
        else:
            i += 1

        bit >>= 1

    return num

def testcase_ends():
    t, num = map(int, raw_input().split())
    if t == 0 and num == 0:
        return 1

    cutcand = []
    curmax = -1
    for i in range(1<<(len(str(num))-1)):
        cut = shred(num, i)
        sumcut = sum(map(int, cut))

        if sumcut > t:
            continue
        elif sumcut == curmax:
            cutcand.append(cut)
        elif sumcut > curmax:
            cutcand = [cut]
            curmax = sumcut

    if len(cutcand) > 1:
        print 'rejected'
    elif len(cutcand) == 1:
        print curmax, ' '.join(cutcand[0])
    else:
        print 'error'

    return 0

def main():
    while True:
        if testcase_ends():
            return 0

if __name__ == '__main__':
    main()

#!/usr/bin/python

def shred(num, bit):
    num = list(str(num))
    i = 0
    while bit:
        if bit & 1:
            num[i] += num[i+1]
            num.pop(i+1)
        else:
            i += 1

        bit >>= 1

    return num

def testcase_ends():
    t, num = map(int, raw_input().split())
    if t == 0 and num == 0:
        return 1

    cut = []
    best = -1
    for i in range(1<<(len(str(num))-1)):
        curcut = shred(num, i)
        cursum = sum(map(int, curcut))
        if cursum > t: continue
        if cursum == best:
            cut.append(curcut)
        elif cursum > best:
            cut = [curcut]
            best = cursum

    if not cut:
        print 'error'
    elif len(cut) > 1:
        print 'rejected'
    else:
        print best, ' '.join(cut[0])

def main():
    while True:
        if testcase_ends():
            return 0

if __name__ == '__main__':
    main()

