while True:
    t, n = input().split()
    t = int(t)
    if t == 0:
        exit()
    L = len(n)
    m = 0
    parts = []
    f = False
    for b in range(1 << (L - 1)):
        c = int(n[0])
        s = 0
        li = []
        for k in range(L - 1):
            if b >> k & 1 == 1:
                s += c
                li.append(c)
                c = 0
            c = 10 * c + int(n[k + 1])
        s += c
        li.append(c)
        c = 0
        if s > t:
            continue
        if s >= m:
            f = (s == m)
            m = s
            parts = li
    if f:
        print('rejected')
    elif m == 0:
        print('error')
    else:
        print(m, *parts, sep=' ')

#!/usr/bin/env python

import sys
import math
import itertools as it
from collections import deque

sys.setrecursionlimit(10000000)

while True:
    N, S = raw_input().split()
    if N == "0" and S == "0":
        break
    N = int(N)
    m = {}
    def func(index, num, lst):
        if index == len(S):
            if num in m:
                m[num] = ["rejected"]
            else:
                m[num] = list(lst)
            return
        for i in range(index + 1, len(S) + 1):
            p = int(S[index:i])
            lst.append(str(p))
            func(i, num + p, lst)
            lst.pop()
    func(0, 0, [])
    score = -1
    ans = []
    for key in m:
        if score < key and key <= N:
            score = key
            ans = " ".join(m[key])
    if score == -1:
        print "error"
    elif ans == "rejected":
        print ans
    else:
        print str(score) + " " + ans
    pass
