using namespace std;

int main() {
	cin.tie(nullptr);
	ios::sync_with_stdio(false);

	for(int m, c, n; cin >> m >> c >> n && m;) {
		vector<unordered_map<int, int>> desks(m);

		queue<int> que;
		vector<int> index(n, 0);
		vector<vector<int>> books(n);

		for(int i = 0; i < n; ++i) {
			que.push(i);

			int k;
			cin >> k;

			books[i].reserve(k);

			for(int j = 0; j < k; ++j) {
				int b;
				cin >> b;
				books[i].emplace_back(b);
			}
		}

		int ans = 0;

		for(int t = 0; !que.empty(); ++t) {
			const int student = que.front();
			que.pop();

			const int id = books[student][index[student]];
			if(++index[student] < books[student].size()) {
				que.push(student);
			}

			int pos = m;
			for(int i = 0; i < m; ++i) {
				if(desks[i].count(id)) {
					desks[i].erase(id);
					pos = i;
					break;
				}
			}

			ans += pos + 1;

			int tmp = m;
			for(int i = 0; i < m; ++i) {
				if(desks[i].size() < c) {
					desks[i].insert({id, t});
					tmp = i;
					break;
				}
			}

			ans += tmp + 1;

			if(tmp != 0) {
				int r_id = -1;
				int r_time = INT_MAX;

				for(const auto &e : desks.front()) {
					if(r_time > e.second) {
						r_id = e.first;
						r_time = e.second;
					}
				}

				ans += 1;
				desks.front().erase(r_id);

				int to = m;
				for(int i = 1; i < m; ++i) {
					if(desks[i].size() < c) {
						desks[i].insert({r_id, r_time});
						to = i;
						break;
					}
				}

				ans += to + 1;

				if(tmp < m) desks[tmp].erase(id);
				ans += tmp + 1;

				desks.front().insert({id, t});
				ans += 1;
			}
		}


		cout << ans << endl;
	}

	return EXIT_SUCCESS;
}

