		}
		int L=0;
		for(int i=0;i<60;i++){
			for(int j=0;j<c;j++)if(sr[j]>i)lis[L++]=req[j][i];
		}
		int t=0;
		for(int i=0;i<L;i++){
				int e=lis[i];
				ret+=at[e];
				if(at[e]<a+1)use[at[e]]--;
				last[e]=t;
				t++;
				if(use[1]<b){
					use[1]++;ret++;at[e]=1;
				}else{
					for(int i=2;i<=a+1;i++){
						if(i==a+1||use[i]<b){
							use[i]++;at[e]=i;ret+=i;break;
						}
					}
					int f=9999999;
					int v=0;
					for(int i=0;i<110;i++)if(at[i]==1){
						if(f>last[i]){f=last[i];v=i;}
					}
					ret++;
					for(int i=2;i<=a+1;i++){
						if(i==a+1||use[i]<b){
							use[i]++;at[v]=i;ret+=i;break;
						}
					}
					use[at[e]]--;ret+=at[e];ret++;at[e]=1;
				}

		}
		printf("%d\n",ret);
	}
}

