int main() {
	while (true) {
		int m, c, n; std::cin >> m >> c >> n; if (n == 0) break;
		std::vector<std::queue<int>> users(n);
		for (auto& u : users) {
			int k; std::cin >> k;
			for (auto i = 0; i < k; ++i) {
				int b; std::cin >> b;
				u.push(b);
			}
		}
		std::vector<int> current_book_position(100, m), recent_access(101, INT_MAX);
		std::set<int> first_desk;
		std::priority_queue<int, std::vector<int>, std::greater<int>> empty_space;
		for (auto i = 0; i <= m; ++i) empty_space.push(i);
		std::vector<int> desk_rest(m + 1, c); desk_rest.back() = INT_MAX;
		int sum_cost = 0;
		std::queue<int> user_in_row; for (auto i = 0; i < n; ++i) user_in_row.push(i);
		for (auto time = 0; !user_in_row.empty(); ++time) {
			const auto top = user_in_row.front(); user_in_row.pop();
			const auto ref = users[top].front(); users[top].pop();
			if (!users[top].empty()) user_in_row.push(top);
			const auto target_pos = current_book_position[ref];
			if (++desk_rest[target_pos] == 1) {
				empty_space.push(target_pos);
			}
			int cost = target_pos + 1;
			if (desk_rest[0] > 0) {
				cost += 1;
				if (--desk_rest[0] == 0) empty_space.pop();
			}
			else {
				const auto temp_area = empty_space.top();
				if (--desk_rest[temp_area] == 0) {
					empty_space.pop();
				}
				const auto most_least_ref = *std::min_element(first_desk.begin(), first_desk.end(), [&recent_access](int a, int b) {return recent_access[a] < recent_access[b]; });
				const auto escape_area = empty_space.top();
				if (--desk_rest[escape_area] == 0) {
					empty_space.pop();
				}
				if (++desk_rest[temp_area] == 1) {
					empty_space.push(temp_area);
				}
				cost += temp_area + 1 + 1 + escape_area + 1 + temp_area + 1 + 1;
				current_book_position[most_least_ref] = escape_area;
				first_desk.erase(most_least_ref);
			}
			current_book_position[ref] = 0;
			first_desk.insert(ref);
			recent_access[ref] = time;
			sum_cost += cost;
		}
		std::cout << sum_cost << '\n';
	}
}

#include<iostream>
