      ans++;
    }
    else{
      REP(i,2,m+2){
        if(desk[i].size() < c || i == m+1){
          desk[i].push_back(book);
          place[book] = i;
          ans += i;
          break;
        }
      }

      int tmp = desk[1][0]; desk[1].erase(desk[1].begin());
      ans++;

      REP(i,2,m+2){
        if(desk[i].size() < c || i == m+1){
          desk[i].push_back(tmp);
          place[tmp] = i;
          ans += i;
          break;
        }
      }

      desk[1].push_back(book);
      p = place[book];
      desk[p].erase(find(desk[p].begin(),desk[p].end(),book));
      place[book] = 1;
      ans += p + 1;
    }

    if(!req[idx].empty()) q.push(idx);
  }

  printf("%d\n",ans);
}

int main(){
  while(scanf("%d%d%d",&m,&c,&n),m||c||n){
    rep(i,13) desk[i].clear();

