    for(int book_i = 0; book_i < max_request; book_i++){
      for(int student_i = 0; student_i < num_of_students; student_i++){
        if(students[student_i].size() <= book_i) continue;
        books.push(students[student_i][book_i]);
      }
    }

    deque<int> desks[101];

    int score = 0;
    while(!books.empty()){
      int id = books.front();
      books.pop();

