using namespace std;
int m,c,n;
vector<int> shelf[32];

int pop(int nx){
  for(int i=0;i<m;i++)
    for(int j=0;j<(int)shelf[i].size();j++)
      if(shelf[i][j] == nx){
	shelf[i].erase(shelf[i].begin()+j);
	return i+1;
      }
  return m+1;
}

int push(int nx){
  int res = m+1;
  for(int i=0;i<m;i++){
    if((int)shelf[i].size()>=c) continue;
    shelf[i].push_back(nx);
    if(i==0) return 1;
    res = i+1;
    break;
  }

  int last = shelf[0][0];shelf[0].erase(shelf[0].begin());
  res++;
  for(int i=1;i<=m;i++){
    if(i==m) {res+=m+1;continue;}
    if((int)shelf[i].size()>=c) continue;
    shelf[i].push_back(last);
    res += i+1;
    break;
  }

  res+=pop(nx);
  shelf[0].push_back(nx);
  return res+1;
}

