      int nx = A[t].back();A[t].pop_back();
      if(!A[t].empty()) Q.push(t);

      int cost = pop(nx);
      cost +=push(nx);
      ans+=cost;
    }

    cout<<ans<<endl;
  }
  return 0;
}

