                    k = check(1,m);
                    q[k].push(a[i][j]);
                    cost += k;
                    //
                    if (k!=1)
                    {
                        num = q[1].front();
                        q[1].pop();
                        cost += 1;

                        kk=check(2,m);
                        if (kk!=m+1)
                        {
                            q[kk].push(num);
                        }
                        cost += kk;
                        //?
                        erase(a[i][j],k);
                        cost += k;

                        q[1].push(a[i][j]);
                        cost += 1;

                    }

                }

        cout << cost << endl;
        cin >> m >> c >> n;
    }
    return 0;
}

