using namespace std;

struct Sectors{
  int start;
  int end;
  int id;
  Sectors() : start(0), end(1000000010), id(-1){}
  Sectors(int s, int e, int i) : start(s), end(e), id(i){}

  int size(){ return end - start + 1; }
  bool isEmpty(){ return id == -1; }
  bool isIn(int sec){ return start <= sec && sec <= end; }
};

int main(){
  int n;
  while(scanf("%d", &n), n){
    list<Sectors> ss;
    int emp = 0;

    ss.push_back(Sectors());

    REP(cc, n){
      char buff[2];
      scanf("%s", buff);
      if(buff[0] == 'W'){
	int l, s;
	scanf("%d%d", &l, &s);

	if(emp == 0){
	  list<Sectors>::iterator it = ss.end(); --it;
	  int start = it->start;
	  int end   = start + s;
	  it->start = end + 1;
	  it = ss.insert(it, Sectors(start, end, l));
	}else{
	  list<Sectors>::iterator it = ss.begin();
	  while(s > 0){
	    if(it->isEmpty()){
	      int sz = it->size();
	      if(sz > s){
		int start = it->start;
		int end   = start + s;
		it->start = end + 1;
		it = ss.insert(it, Sectors(start, end, l));
		break;
	      }else{
		s -= sz;
		it->id = l;
		emp--;
	      }
	    }

	    assert(it != ss.end());
	    ++it;
	  }
	}
      }else if(buff[0] == 'D'){
	int l;
	scanf("%d", &l);
	FOR(it, ss){
	  if(it->id == l){
	    it->id = -1;
	    emp++;
	  }
	}
      }else{
	int sec;
	scanf("%d", &sec);

	FOR(it, ss){
	  if(it->isIn(sec)){
	    printf("%d\n", it->id);
	    break;
	  }
	}
      }

      // FOR(it, ss)printf("[%d,%d]:%d ", it->start, it->end, it->id);puts("");
    }
    puts("");
  }
  return 0;
}

// Book Replacement
// http://judge.u-aizu.ac.jp/onlinejudge/description.jsp?id=1258

#include<iostream>
#include<vector>
#include<queue>
#include<algorithm>

#define ALL(v) (v).begin(),(v).end()

using namespace std;

int M, C, N;
vector<vector<int>> desks;
vector<queue<int>> students;

bool is_full(int i) {
    return desks[i].size() == C;
}

int move_book(int book_num) {
    for (int i = 1; i < M; ++i) {
        if (!is_full(i)) {
            desks[i].push_back(book_num);
            return i + 1;
        }
    }
    return M + 1;
}

int lend_book(int book_num) {
    for (int i = 0; i < M; ++i) {
        if (find(ALL(desks[i]), book_num) != desks[i].end()) {
            desks[i].erase(find(ALL(desks[i]), book_num));
            return i + 1;
        }
    }
    return M + 1;
}

int return_book(int book_num) {
    int cost = 0;

    if (!is_full(0)) {
        desks[0].push_back(book_num);
        return 1;
    }

    cost += move_book(book_num);

    int old_book_num = desks[0].front();
    cost += 1;
    desks[0].erase(find(ALL(desks[0]), old_book_num));
    cost += move_book(old_book_num);

    for (int i = 1; i < M; ++i) {
        if (find(ALL(desks[i]), book_num) != desks[i].end()) {
            desks[i].erase(find(ALL(desks[i]), book_num));
            cost += i + 1;
            break;
        }
        if (i == M-1) cost += M + 1;
    }

    if (M == 1) cost += M + 1;
    desks[0].push_back(book_num);
    cost += 1;

    return cost;
}

int main() {
    while (true) {
        cin >> M >> C >> N;
        if (!M && !C && !N) break;
        desks.assign(M, vector<int>());
        students.assign(N, queue<int>());

        int max_turn = 0;
        for (int nth = 0; nth < N; ++nth) {
            int K;
            cin >> K;
            for (int i = 0; i < K; ++i) {
                int book_num;
                cin >> book_num;
                students[nth].push(book_num);
            }
            max_turn = max(max_turn, (int)students[nth].size());
        }

        int total_cost = 0;
        for (int turn = 0; turn < max_turn; ++turn) {
            for (int nth = 0; nth < N; ++nth) {
                if (students[nth].empty()) continue;
                int book_num = students[nth].front();
                students[nth].pop();
                total_cost += lend_book(book_num); 
                total_cost += return_book(book_num);
            }
        }

        cout << total_cost << endl;
    }
}

