using namespace std;

int M,C,N;

map<int,int> desk[111];
int take(int bk,int &res){  
  for(int i=0;i<M;i++){
    if( desk[i].count(bk) ) {
      res += i+1;
      desk[i].erase(bk);
      return i;
    }
  }
  res += M+1;
  return M;
}
int put(int bk,int cnt,int st,int &res){
  for(int i=st;i<M;i++){
    if( (int)desk[i].size() < C ) {
      res += i+1;
      desk[i][bk] = cnt;
      return i;      
    }
  }
  res += M+1;
  return M;
}

pair<int,int> mink(map<int,int> &mp){
  auto p = make_pair(0,(1<<19));
  for( auto it = mp.begin(); it != mp.end(); it++ ){
    if( p.second > it->second ) p = *it;
  }
  return p;
}

int main(){
  cin.tie(0);
  ios::sync_with_stdio(false);

  while( cin >> M >> C >> N && (M|C|N) ){

    queue<int> q;
    queue<int> hq[111];


    for(int i=0;i<N;i++){
      int k; cin >> k;
      for(int j=0;j<k;j++){
        int b; cin >> b; hq[i].push(b);
      }
      q.push(i);
    }
    int res = 0;
    int cnt = 0;
    while( !q.empty() ) {
      cnt++;
      int p = q.front(); q.pop();
      if( hq[p].empty() ) continue;
      int bk = hq[p].front(); hq[p].pop();


      take(bk,res);
      int pid = put(bk,cnt,0,res);
      if( pid != 0 ){
        auto dt = mink(desk[0]);
        take( dt.first, res );
        put( dt.first, dt.second, 1, res );

        take( bk,res);
        assert( put(bk, cnt, 0, res) == 0 );
      }
      //cout << cnt << ": " << res << endl;
      q.push( p );
    }
    for(int i=0;i<=M;i++) desk[i].clear();
    cout << res << endl;
  }
}

