using namespace std;
typedef list<int>L;
typedef L::iterator I;
typedef vector<int>V;
L d[30];
int F(int b,int m,int x,I*p)
{
	int i;
	for(i=b;i<m;++i)if((*p=find(d[i].begin(),d[i].end(),x))!=d[i].end())break;
	return i;
}
int G(int b,int m,int c)
{
	int i;
	for(i=b;i<m;++i)if(d[i].size()<c)break;
	return i;
}
int main()
{
	int m,c,n,k,b,r,i,j;
	L::iterator ite;
	V u[100],v;
	v.reserve(5000);
	while(scanf("%d%d%d",&m,&c,&n),m)
	{
		for(i=0;i<m;++i)d[i].clear();
		v.clear();
		for(i=0;i<n;++i)
		{
			u[i].clear();
			for(scanf("%d",&k);k--;)
			{
				scanf("%d",&b);
				u[i].push_back(b);
			}
		}
		for(i=0;i<50;++i)for(j=0;j<n;++j)if(i<u[j].size())v.push_back(u[j][i]);
		for(r=i=0;i<v.size();++i)
		{
			k=F(0,m,v[i],&ite);
			r+=k+1;
			if(k!=m)d[k].erase(ite);
			if(d[0].size()<c)
			{
				d[0].push_back(v[i]);
				++r;
			}
			else
			{
				j=G(1,m,c);
				r+=j+1;
				if(j!=m)d[j].push_back(v[i]);
				k=G(1,m,c);
				if(k!=m)d[k].push_back(d[0].front());
				r+=k+1;
				d[0].pop_front();
				if(j!=m)
					d[j].erase(find(d[j].begin(),d[j].end(),v[i]));
				d[0].push_back(v[i]);
				r+=j+3;
			}
		}
		printf("%d\n",r);
	}
	return 0;
}

#include<bits/stdc++.h>
using namespace std;

int M,C,N;
int D[20][111];
int Ds[20];
int used[111];
int pl[111];
int res;
const int MAX = 101;
vector<int> B[111];
void init(){
  memset( D, 0, sizeof( D ) );
  memset( Ds,0,sizeof( Ds ) );
  memset( pl,0,sizeof( pl ) );
  memset(used,0,sizeof(used));
  res = 0;
  used[MAX] = (1<<29);
}

void put(int q,int id){
  Ds[id]++;
  D[id][q] = 1;
  pl[q] = id;
  res += id+1;
}

void take(int q,int id){
  Ds[id]--;
  D[id][q] = 0;
  pl[q] = -1;
  res += id+1;
}
bool pcheck(int id){
  if( id==M ) return true;
  return Ds[id] < C;
}

int main(){
  while( cin >> M >> C >> N && (M||C||N) ){

    init();
    for(int i=0;i<N;i++){
      int k; cin >> k;
      B[i].resize(k);
      for( int &b : B[i] ) {
        cin >> b;
        D[M][b] = 1;
        pl[b] = M;
      }
    }
    vector<int> query;
    for(int j=0;j<=50;j++){
      for(int i=0;i<N;i++){
        if( (int)B[i].size() <= j ) continue;
        query.push_back( B[i][j] );
      }
    }

    int cnt=1;
    res = 0;
    for( int q: query ){
      used[q] = cnt++;
      for(int i=0;i<=M;i++){
        if( pcheck( i ) || i == pl[q] ){
          take( q, pl[q] );
          put( q, i );          
          break;
        }
      }
      if( pl[q] == 0 ) continue;
      int maxid = MAX;
      for(int i=0;i<=100;i++){
        if( D[0][i] )
          if( used[maxid] > used[i] ) maxid = i;        
      }
      for(int i=1;i<=M;i++){
        if( pcheck( i ) ){          
          take( maxid, pl[maxid] );
          put( maxid, i );    
          break;
        }        
      }
      take( q, pl[q] );
      put( q, 0 );
    }
    cout << res << endl;

  }
}

