using namespace std;

const int MAXN = 105;
const int MAXK = 52;
const int MAXM = 12;
const int INF = 1<<28;
int M, C, N;
int K[MAXN];
int B[MAXN][MAXK];
vector<int> D[MAXM];
int size[MAXM];
int pos[MAXN];

int tottekuru(int req) {
  int res = 0;
  for(int i = 0; i < M; ++i) {
    vector<int>::iterator it
      = find(D[i].begin(), D[i].end(), req);
    if(it != D[i].end()) {
      D[i].erase(it);
      res += i+1;
      break;
    }
  }

  if(D[0].size() < size[0]) {
    D[0].push_back(req);
    res += 1;
    return res;
  }

  int i, j;
  for(i = 1; i < M; ++i) {
    if(D[i].size() < size[i]) {
      break;
    }
  }
  for(j = i; j < M; ++j) {
    if(D[j].size() + (i == j) < size[j]) {
      break;
    }
  }
  int tmp = *D[0].begin();
  D[0].erase(D[0].begin());
  D[j].push_back(tmp);
  D[0].push_back(req);
  res += i+1 + 1 + j+1 + i+1 + 1;
  return res;
}

int main() {
  while(cin >> M >> C >> N && (M|C|N)) {
    set<int> s;
    for(int i = 0; i < N; ++i) {
      cin >> K[i];
      for(int j = 0; j < K[i]; ++j) {
        cin >> B[i][j];
        s.insert(B[i][j]);
      }
    }
    ++M;
    for(int i = 0; i < M; ++i) {
      if(i+1 == M) size[i] = INF;
      else size[i] = C;
    }
    for(int i = 0; i < MAXM; ++i) D[i].clear();
    D[M-1] = vector<int>(s.begin(), s.end());

    fill(pos, pos+MAXN, 0);
    int res = 0;
    while(1) {
      bool flag = false;
      for(int i = 0; i < N; ++i) {
        if(pos[i] == K[i]) continue;
        flag = true;
        int req = B[i][pos[i]++];
        res += tottekuru(req);
      }
      if(!flag) break;
    }
    cout << res << endl;
  }
  return 0;
}

