#include <cstdio>
#include <queue>
using namespace std;

#define INF 1000
int D[10][30],M,C,N;

bool check(int p, int v)
{
	for(int i=0; i<C; i++)
	{
		if(D[p][i]==v)
		{
			for(int j=i; j<C-1; j++)
				D[p][j]=D[p][j+1];

			D[p][C-1]=INF;
			return true;
		}
	}

	return false;
}

void set(int p, int v)
{
	for(int i=0; i<C; i++)
	{
		if(D[p][i]==INF)
		{
			D[p][i]=v;
			return;
		}
	}
}

int isempty()
{
	for(int i=0; i<M; i++)
	for(int j=0; j<C; j++)
		if(D[i][j]==INF) return i+1;

	return -1;
}

int main()
{
	while(scanf("%d%d%d", &M,&C,&N), (M||C||N))
	{
		for(int i=0; i<M; i++)
		for(int j=0; j<C; j++)
			D[i][j]=INF;

		queue<queue<int> > q;
		for(int i=0; i<N; i++)
		{
			int K;
			scanf("%d", &K);
			queue<int> tq;
			for(int j=0; j<K; j++)
			{
				int t;
				scanf("%d", &t);
				tq.push(t);
			}
			q.push(tq);
		}

		int ret=0;
		while(!q.empty())
		{
			queue<int> tq=q.front(); q.pop();
			int id=tq.front(); tq.pop();

			bool g=false;
			for(int i=0; i<M; i++)
			{
				if(check(i,id))
				{
					ret+=i+1;
					g=true;
					break;
				}
			}

			if(!g)
			{
				ret+=M+1;
			}

			if(check(0,INF)) 
			{
				set(0,id);
				ret++;
			}
			else
			{
				int tp=0;
				g=false;
				for(int i=0; i<M; i++)
				{
					if(check(i,INF))
					{
						set(i,id);
						tp=i+1;

						ret+=tp;

						g=true;
						break;
					}
				}
				if(!g) 
				{
					ret+=M+1;
					ret++;
					ret+=M+1;
					ret+=M+1;
					ret++;
					check(0,D[0][0]);
					set(0,id);
				}
				else
				{
					int f=isempty();
					check(tp-1, id);
					if(f==-1)
					{
						ret++;
						ret+=M+1;
						check(0, D[0][0]);
						set(0, id);
						ret++;
						ret+=tp;
					}
					else
					{
						ret++; 
						ret+=f;		 
						int tt=D[0][0];
						check(0,D[0][0]);
						set(0,id);
						set(f-1, tt);

						ret+=tp;  

						ret++;
					}
				}
			}

			if(!tq.empty()) q.push(tq);
		}

		printf("%d\n", ret);
	}
}

