public class Main {
	Scanner sc = new Scanner(System.in);

	void run() {
		for (;;) {
			int m = sc.nextInt();
			int c = sc.nextInt();
			int n = sc.nextInt();

			if ((m | c | n) == 0) {
				break;
			}

			int[][] qq = new int[n][];

			int sz = 0;
			int maxx = 0;
			for (int i = 0; i < n; i++) {
				int x = sc.nextInt();
				qq[i] = new int[x];
				for (int j = 0; j < x; j++) {
					qq[i][j] = sc.nextInt();
				}
				maxx = Math.max(x, maxx);
				sz += x;
			}

			int[] q = new int[sz];
			int cnt = 0;
			for (int j = 0; j < maxx; j++) {
				for (int i = 0; i < n; i++) {
					if (qq[i].length <= j) {
						continue;
					}
					q[cnt] = qq[i][j];
					cnt++;
				}
			}

			int map[] = new int[100];

			LinkedList<Integer> l = new LinkedList<Integer>();

			int smp[] = new int[m + 2];

			Arrays.fill(smp, c);
			smp[m + 1] = 1000;

			int cost = 0;
			for (int i = 0; i < sz; i++) {

				// get
				if (map[q[i]] == 0) {
					cost += m + 1;
				} else {
					if (map[q[i]] == 1) {
						l.remove((Integer) q[i]);
					}
					cost += map[q[i]];
					smp[map[q[i]]]++;
				}

				l.addFirst(q[i]);
				map[q[i]] = 1;

				if (l.size() > c) { // swap
					int j = 2;
					int k;
					for (; smp[j] == 0; j++)
						;
					smp[j]--;

					k = j;
					int v = l.pollLast();

					cost += k; // put k temp

					cost += 1; // remove 1
					for (; smp[j] == 0; j++)
						;
					smp[j]--;

					cost += j; // 1 to j
					map[v] = j;

					cost += k; // k to 1;
					cost += 1;

					smp[k]++;
				} else {
					cost += 1;
				}
				// return
			}
			System.out.println(cost);
		}
	}

	public static void main(String[] args) {
		Main m = new Main();
		m.run();
	}
}

import java.util.*;
import static java.lang.Math.*;

public class Main {
	final Scanner sc=new Scanner(System.in);
	public static void main(String[] args) {
		new Main().init();
	}
	void init(){
		new AOJ1258();
	}

	class AOJ1258{
		final int INF=Integer.MAX_VALUE/4;
		AOJ1258(){
			while(true){
				int M=sc.nextInt(),C=sc.nextInt(),N=sc.nextInt();
				if(((M|C)|N)==0)	break;
				System.out.println(solve(M,C,N));
			}
		}
		int solve(int M,int C,int N){
			LinkedList<LinkedList<Integer>> q=new LinkedList<LinkedList<Integer>>();
			for(int i=0; i<N; i++){
				q.add(new LinkedList<Integer>());
				int K=sc.nextInt();
				for(int j=0; j<K; j++)	q.getLast().add(sc.nextInt());
			}
			Store store=new Store(M,C);
			int ans=0;
			while(!q.isEmpty()){
				int r=q.getFirst().poll();
				ans+=store.take(r);
				ans+=store.put(r);
				//debug
				//System.out.println(store);
				if(q.getFirst().isEmpty())	q.removeFirst();
				else	q.addLast(q.poll());
			}
			return ans;
		}
		class Store{
			int M,C,time;
			ArrayList<ArrayList<Book>> desks;
			Store(int m,int c){
				this.M=m;	this.C=c;	this.time=0;
				desks=new ArrayList<ArrayList<Book>>();
				for(int i=0; i<M; i++)	desks.add(new ArrayList<Book>());
			}
			int take(int r){
				int ret=M+1;
				for(int i=0; i<M; i++){
					for(int j=0; j<desks.get(i).size(); j++){
						if(desks.get(i).get(j).id==r){
							desks.get(i).remove(j);
							ret=i+1;
							break;
						}
					}
					if(ret<M+1)	break;
				}
				return ret;
			}
			int put(int r){
				int ret=M+1;
				if(desks.get(0).size()<C){
					desks.get(0).add(new Book(r,time++));
					ret=1;
				}else{
					ret=0;
					Pair tmpIdx=new Pair(M,0);
					for(int i=1; i<M; i++){
						if(desks.get(i).size()<C){
							tmpIdx.first=i;
							desks.get(i).add(new Book(INF,INF));
							tmpIdx.second=desks.get(i).size()-1;
							break;
						}
					}
					ret+=tmpIdx.first+1;
					int oldestTime=desks.get(0).get(0).t,oldestIdx=0;
					for(int i=1; i<desks.get(0).size(); i++){
						if(oldestTime>desks.get(0).get(i).t){
							oldestTime=desks.get(0).get(i).t;
							oldestIdx=i;
						}
					}
					Book oldest=desks.get(0).remove(oldestIdx);
					ret+=1;
					int empIdx=M;
					for(int i=1; i<M; i++){
						if(desks.get(i).size()<C){
							desks.get(i).add(oldest);
							empIdx=i;
							break;
						}
					}
					ret+=empIdx+1;
					if(tmpIdx.first<M)	desks.get(tmpIdx.first).remove(tmpIdx.second);
					ret+=tmpIdx.first+1;
					desks.get(0).add(new Book(r,time++));
					ret+=1;
				}
				return ret;
			}
			@Override public String toString(){return desks.toString();}
		}
		class Book{
			int id,t;
			Book(int id,int t){this.id=id;this.t=t;}
			@Override public String toString(){return "ID"+id+" T"+t;}
		}
		class Pair implements Comparable<Pair>{
			int first,second;
			Pair(int first,int second){this.first=first; this.second=second;}
			@Override public String toString(){return "("+first+","+second+")";}
			@Override public int compareTo(Pair p){
				if(this.first<p.first)	return -1;
				if(this.first>p.first)	return 1;
				if(this.second<p.second)	return -1;
				if(this.second>p.second)	return 1;
				return 0;
			}
			@Override public int hashCode(){
				return 17+31*first+second;
			}
			@Override public boolean equals(Object obj){
				if(this==obj)	return true;
				if(obj==null)	return false;
				if(this.getClass() != obj.getClass())	return false;
				Pair p=(Pair)obj;
				if(this.first==p.first&&this.second==p.second)	return true;
				return false;
			}
		}
	}

	class AOJ1262{
		final double INF=Double.MAX_VALUE/4.0;
		AOJ1262(){
			while(true){
				int N=sc.nextInt();
				if(N==0)	break;
				System.out.printf("%.4f\n",solve(N));
			}
		}
		double solve(int N){
			int[] a=new int[N+1];
			for(int i=1; i<=N; i++)	a[i]=sc.nextInt();
			double b=sc.nextDouble();
			int r=sc.nextInt();
			double v=sc.nextDouble(),e=sc.nextDouble(),f=sc.nextDouble();
			double[][] dp=new double[N+1][N+1];
			for(int i=1; i<=N; i++)for(int j=0; j<=N; j++)dp[i][j]=INF;
			//flash(dp,N);
			double tmp=0.0;
			for(int i=0; i<a[1]; i++)	tmp+=exp(i,r,v,e,f);
			dp[1][0]=tmp;
			for(int w=1; w<N; w++){
				for(int h=0; h<w; h++){
					int d=a[w+1]-a[w],x=a[w]-a[h];
					double exp=0.0;
					for(int i=0; i<d; i++)	exp+=exp(x++,r,v,e,f);
					//debug
					//System.out.println(exp);
					dp[w+1][h]=dp[w][h]+exp;
					double exp2=0.0;
					for(int i=0; i<d; i++)	exp2+=exp(i,r,v,e,f);
					dp[w+1][w]=min(dp[w+1][w], dp[w][h]+exp2+b);
				}
				//debug
				//System.out.println("new "+dp[w+1][w]);
			}
			//flash(dp,N);
			double ans=INF;
			for(int i=0; i<=N; i++)	ans=min(ans,dp[N][i]);
			return ans;
		}
		double exp(int x,double r,double v,double e,double f){
			return x>=r?(1/(v-e*(x-r))):(1/(v-f*(r-x)));
		}
		void flash(double[][] dp,int N){
			for(int y=0; y<=N; y++){
				for(int x=0; x<=N; x++)	System.out.printf("%3.3f ",dp[x][y]>=INF?-1:dp[x][y]);
				System.out.println();
			}
		}
	}
}

import java.util.*;
public class Main {
	//0010 start
	//0051 sample match
	int m,c,n;

	private void doit(){
		Scanner sc = new Scanner(System.in);
		while(true){
			m = sc.nextInt();
			c = sc.nextInt();
			n = sc.nextInt();
			if((m|c|n) == 0) break;
			ArrayList<ArrayList<Integer>> list = new ArrayList<ArrayList<Integer>>();
			ArrayList<ArrayList<Integer>> store = new ArrayList<ArrayList<Integer>>();
			for(int i = 0 ; i < m + 1; i++){
				store.add(new ArrayList<Integer>());
			}
			int maxk = 0;
			for(int i = 0 ; i < n; i++){
				int k = sc.nextInt();
				maxk = Math.max(maxk, k);
				list.add(new ArrayList<Integer> ());
				for(int j = 0 ; j < k; j++){
					int b = sc.nextInt();
					list.get(i).add(b);
					store.get(m).add(b);
				}
			}
			int sum = 0;
			int [] res = new int[10];
			for(int i = 0 ; i < maxk; i++){
				for(int j = 0 ; j < n; j++){
					if(list.get(j).size() <= i) continue;
					int num = list.get(j).get(i);
					res[0] = find(store, num);
					sum += res[0];

					res[1] = put(store, num);
					sum += res[1];
					if(res[1] != 1){
						int num2 = store.get(0).get(0);
						sum++;
						res[2] = put(store, num2);
						sum += res[2];
						store.get(0).remove(0);

						res[3] = find(store, num);
						sum += res[3];
						res[4] = put(store, num);
						sum += res[4];
					}
				}
			}
			System.out.println(sum);
		}
	}

	private int put(ArrayList<ArrayList<Integer>> store, int num) {
		for(int i = 0 ; i < store.size(); i++){
			if(store.get(i).size() < c || i == store.size()-1){
				store.get(i).add(num);
				return i+1;
			}
		}
		return -1;
	}

	private int find(ArrayList<ArrayList<Integer>> store, int num) {
		for(int i = 0 ; i < store.size(); i++){
			for(int j = 0; j < store.get(i).size(); j++){
				if(store.get(i).get(j) == num){
					store.get(i).remove(j);
					return i + 1;
				}
			}
		}
		return -1;
	}

	public static void main(String [] args){
		new Main().doit();
	}
}

import java.util.HashSet;
import java.util.LinkedList;
import java.util.Queue;
import java.util.Scanner;
import java.util.Set;

//Book Replacement
public class Main{

	@SuppressWarnings("unchecked")
	void run(){
		Scanner sc = new Scanner(System.in);
		for(;;){
			int m = sc.nextInt(), c = sc.nextInt(), n = sc.nextInt();
			if((m|c|n)==0)break;
			int[] k = new int[n];
			int[][] req = new int[n][50];
			for(int i=0;i<n;i++){
				k[i] = sc.nextInt();
				for(int j=0;j<k[i];j++)req[i][j]=sc.nextInt();
			}
			Queue<Integer> D1 = new LinkedList<Integer>();
			Set<Integer>[] d = new Set[m+1];
			for(int i=2;i<=m;i++)d[i]=new HashSet<Integer>();
			int res = 0;
			for(int j=0;j<50;j++)for(int i=0;i<n;i++){
				if(k[i]<=j)continue;
				int r = req[i][j];
				if(D1.contains(r)){
					res += 2; D1.remove(r); D1.add(r);
					continue;
				}
				boolean shelf = true;
				for(int a=2;a<=m;a++){
					if(d[a].contains(r)){
						shelf = false;
						d[a].remove(r); res+=a;
						break;
					}
				}
				if(shelf)res+=m+1;
				if(D1.size()<c){
					res+=1; D1.add(r);
					continue;
				}
				int pos = -1;
				for(int a=2;a<=m;a++){
					if(d[a].size()<c){
						pos = a; res+=a; d[a].add(r); break;
					}
				}
				if(pos==-1)res+=m+1;
				int old = D1.poll();
				res+=1;
				shelf = true;
				for(int a=2;a<=m;a++){
					if(d[a].size()<c){
						shelf = false;
						d[a].add(old); res+=a;
						break;
					}
				}
				if(shelf)res+=m+1;
				if(pos!=-1){
					d[pos].remove(r); res+=pos;
				}
				else res+=m+1;
				D1.add(r); res+=1;
			}
			System.out.println(res);
		}
	}

	public static void main(String[] args) {
		new Main().run();
	}
}
