vector<ll> enumdiv(ll x) {
    vector<ll> res;
    for (ll i = 1; i * i <= x; i++) {
        if (x % i == 0) {
            res.emplace_back(i);
            if (i != x / i) res.emplace_back(x / i);
        }
    }
    sort(res.begin(), res.end());
    return res;
}

void solve() {
    ll m, n; scanf("%lld %lld", &m, &n);
    ll t = m * m + n * n;
    auto d = enumdiv(t);
