        if (e == 1 or e == t) continue;
        if (g[e].size() > 0 and t / e != 1 and t /e != t) {
            for (auto p : g[e]) {
                for (auto q : g[t / e]) {
                    if (p.first * q.first - p.second * q.second == m and p.first * q.second + p.second * q.first == n) {
                        puts("C");
                        return;
                    }
                }
            }
        }
    }
    puts("P");
}

