int main() {
    int n;
    cin >> n;

    for(int i=0; i< n; i++){
        int p, q;
        cin >> p >> q;

        int mul1 = p*p + q*q;

        int x = 1;
        int y = 1;
        bool flag = false;
        int ans;

        while(1){
            while(1){
                int mul2 = x*x + y*y;
                if(mul2 < mul1){

                    if (mul1 % mul2 == 0){
                        flag = true;
                        break;
                    }

                }
                else{
                    break;
                }
                ++y;
            }
            if(flag){
                break;
            }
            if(x*x >= 20000 || x*x > mul1) break;

            ++x;
            y = 0;
        }

        if(flag){
            cout << "C" << endl;
        }
        else{
            cout << "P" << endl;
        }
    }
    return 0;
}

