#define for_(i,a,b) for(int i=a;i<b;++i)
#define allof(a) a.begin(),a.end()
#define minit(a,b) memset(a,b,sizeof(b))

int T;
int m, n;

void solve() {
	int s = m * m + n * n;
	if (s <= 1) {
		cout << "C" << endl;
		return;
	}

	int range = (int)ceil(sqrt(s));

	int cnt = 0;
	for_(i,-range,range + 1) for_(j,-range,range+1) {
		if (i == 0 && j == 0) continue;
		int div = i * i + j * j;
		int mul1 = i * m + j * n, mul2 = i * n - j * m;

		if ((mul1 + div) % div == 0 && (mul2 + div) % div == 0) cnt++;
	}

	cout << ( (cnt == 8) ? "R" : "C") << endl;
}

int main() {
	cin >> T;
	for_(i,0,T) {
		cin >> m >> n;
		solve();
	}
	return 0;
}

