vector<pair<int,int> > q[20000];
int gcd(int x,int y){return y==0?x:gcd(y,x%y);}
bool judge(int m,int n,int i,int j){
	if(gcd(i*m+j*n,i*n-j*m)==i*i+j*j)return true;
	else return false;
}
int main(){
	for(int i=-141;i<=141;i++){
		for(int j=-141;j<=141;j++){
			if(i*i+j*j<20000&&i*i+j*j>0){
				q[i*i+j*j].push_back(make_pair(i,j));
			}
		}
	}
	int T;
	scanf("%d",&T);
	while(T--){
		int m,n;
		scanf("%d%d",&m,&n);
		int s=m*m+n*n;
		bool find=false;
		for(int j=2;j*j<=s;j++){
			if(s%j==0){
				for(int i=0;i<q[j].size();i++){
					if(judge(m,n,q[j][i].first,q[j][i].second)){
						find=true;
						break;
					}
				}
				if(find)break;
				for(int i=0;i<q[s/j].size();i++){
					if(judge(m,n,q[s/j][i].first,q[s/j][i].second)){
						find=true;
						break;
					}
				}
				if(find)break;
			}
		}
		if(find)printf("C\n");
		else printf("P\n");
	}
}

