const int MAXM = 170;
int main()
{
    int num; cin >> num;

    while(num--)
    {
        int p, q; cin >> p >> q;
        int count = 0, seki = 0;
        for(int m = 0; m < MAXM; m++)for(int n = 0; n < MAXM; n++)
        {
            if((m == 1 && n == 0) ||( m == 0 && n == 1)) continue;
            //cout << m << " " << n << endl;
            seki = m * m + n * n;
            if(seki == 0) continue;
            if(seki >= 20000 ) continue;
            if((((m * p + n * q) % seki) == 0) && (((m * q - n * p) % seki) == 0))
            {
                //if((m == abs(p) && (n == abs(q))) || (m == abs(q) && (n == abs(p)) ) )
                if((m == p && n == q) ||
                        (m == -q && n == p) ||
                        (m == -p && n == -q) ||
                        (m == q && n == -p))
                {
                    continue;

                    //cout << m << " " << n << endl;    
                }
                count ++;
            }
        }
        if(count>0) cout << 'C' << endl;
        else cout << 'P' << endl;
    }
    return 0;
}

