int absmax(int p, int q) {
	int a, b;
	a = p;
	b = q;
	if( a < 0 ) a *= -1;
	if( b < 0 ) b *= -1;
	if( a < b ) return b;
	return a;
}

struct pairs {
	int p, q;
	bool operator ==(const pairs &right) const{
		return ( p == right.p && q == right.q );
	}
	bool operator <(const pairs &right) const{
		return ( p == right.p ? q < right.q : p < right.p );
	}
	pairs operator *(const pairs &right) const{
		pairs ret;
		ret.p = p * right.p - q * right.q;
		ret.q = p * right.q + q * right.p;
		return ret;
	}
};

int main(void){
//	FILE* fp_in = freopen("data.txt", "r", stdin);
	int N;
	cin >> N;
	string ans[] = { "C", "P" };

	for(int i = 0; i < N; i++) {
		pairs p, q;
		vector< pairs > sp;
		cin >> p.p >> p.q;
		for( q.p = - absmax(p.p, p.q) ; q.p <= absmax(p.p, p.q); q.p ++) {
			for( q.q = - absmax(p.p, p.q); q.q <= absmax(p.p, p.q); q.q ++) {
				int tmp = q.p * q.p + q.q * q.q;
				if( tmp > 0 && ( ( q.p * p.p + q.q * p.q ) % tmp == 0 ) && 
					( q.p * p.q - q.q * p.p ) % tmp == 0 ) {
						sp.push_back( q );
				}
			}
		}
		cout << ans[sp.size() == 8] << endl;
	}
//	while(1){}
	return 0;
}

