const int N = 20005;
int n,m,ed;
int sq[N];
bool cal(int a,int b){
	int c = m*b + n*a;
	int d = m*b - n*a;
	if(c % (a*a + b*b)) return false;
	else c /= a*a + b*b;
	if(a*b == 0){
		if(d != 0) return false;
	}
	else if(d % (a*b*2)) return false;
	else d /= a*b*2;
	int x = a*a + b*b;
	int y = c*c + d*d;
	return x!=1 && x!=ed && y!=1 && y!=ed;
}
bool chk(int c){
	for(int a=0;a*a<=c;a++)if(sq[c - a*a] != -1){
		int b = sq[c - a*a];
		if(cal(a,b)) return true;
	}
	return false;
}
bool solve(){
	int c = n*n + m*m;
	for(int i=2;i*i<=c;i++)if(c % i == 0){
		if(chk(i)) return true;
	}
	return false;
}
int main(){
	//freopen("in.txt","r",stdin);
	memset(sq,-1,sizeof sq);
	for(int i=0;i*i<N;i++) sq[i*i] = i;
	int T;
	scanf("%d",&T);
	while(T--){
		scanf("%d%d",&n,&m);
		ed = n*m;
		if(solve()) puts("C");
		else puts("P");
	}
	return 0;
}

