    Point operator + (const Point &a) const {
        return Point(x + a.x, y + a.y, z + a.z);
    }
    Point operator - (const Point &a) const {
        return Point(x - a.x, y - a.y, z - a.z);
    }
    Point operator * (const double k) const {
        return Point(x * k, y * k, z * k);
    }
    Point operator / (const double k) const {
        return Point(x / k, y / k, z / k);
    }
    void read() {
        scanf("%lf%lf%lf", &x, &y, &z);
    }
};
double dot(const Point &a, const Point &b) {
    return a.x * b.x + a.y * b.y + a.z * a.z;
}
Point cross(const Point &a, const Point &b) {
    return Point(a.y*b.z-a.z*b.y, a.z*b.x-a.x*b.z, a.x*b.y-a.y*b.x);
}
double length(const Point& a) {
    return sqrt(dot(a, a));
}

Point c[N];
Point T[N];
Point E;
double r[N];
double tb[N];
bool vis[N];
int n, m, k;
double ans;

double disToSeg(Point p, Point a, Point b) {
    Point v1 = b - a, v2 = p - a, v3 = p - b;
    if (dcmp(dot(v1, v2)) < 0) return length(v2);
    else if (dcmp(dot(v1, v3)) > 0) return length(v3);
    else return length(cross(v1, v2)) / length(v1);
}
bool dang(int j, int i) {

    int d1 = dcmp(length(T[i] - c[j]) - r[j]);
//printf("%d %d %f %f %f %f\n", j, i, disToSeg(c[j], E, T[i]), length(T[i] - c[j]), length(E - c[j]), r[j]);
    int d2 = dcmp(length(E - c[j]) - r[j]);
    if ( d1 <= 0 && d2 <= 0 ) return false;
    if (d1 * d2 < 0) return true;
    return dcmp(disToSeg(c[j], E, T[i]) - r[j]) < 0;
}
void solve(int s) {
    int cnt = 0;
    double res = 0;
    memset(vis, 0, sizeof(vis));
    for (int i = 0; i < m; i++) if ((s >> i) & 1) {
        for (int j = 0; j < n; j++) if (!vis[j]) {
            if (dang(j, i)) {
                vis[j] = true;
                cnt++;
                if (cnt > k) return;
            }
        }
        res += tb[i];
    }
    ans = max(ans, res);
}
void work() {
    for (int i = 0; i < n; i++) {
        c[i].read();
        scanf("%lf", &r[i]);
    }
    for (int i = 0; i < m; i++) {
        T[i].read();
        scanf("%lf", &tb[i]);
        tb[i] /= dot(T[i] - E, T[i] - E);
        printf("@ %f\n", tb[i]);
    }
    E.read();
    ans = 0;
    for (int i = 0; i < (1 << m); i++)
        solve(i);
    printf("%.10f\n", ans);
}
int main() {
freopen("data.in", "r", stdin);
    while (scanf("%d%d%d", &n, &m, &k), n || m || k) {
        work();
    }
    return 0;
}
