struct GN {
  int m, n;

  GN(int m, int n) {
    this->n = n;
    this->m = m;
  }

  GN mult(GN gn2) {
    int x = gn2.m, y = gn2.n;
    return GN(m*x - n*y, m*y - n*x);
  }

  bool isdivof(GN gn1){
    int p = gn1.m, q = gn1.n;
    int tmp = m * m + n * n;
    if(tmp == 0) return false;
    return ((m*p + n*q) % tmp == 0) && ((m*q - n*p) % tmp == 0);
  }

  bool isprimedivof(GN gn1) {
    int x = gn1.m, y = gn1.n;
    return ((m == 1 && n == 0) || (m == 0 && n == 1) ||
            (m == -1 && n == 0) || (m == 0 && n == -1) ||
            (m == x && n == y) || (m == -y && n == x) ||
            (m == -x && n == -y) || (m == y && n == -x));
  }
};

GN operator*(GN gn1, GN gn2) {
  return gn1.mult(gn2);
}


int main () {
  cin.tie(0);
  cout << fixed << setprecision(10);

  int q; cin >> q;
  while(q > 0) {
    int m, n; cin >> m >> n;
    GN gn1 = GN(m, n);
    int tmp = m * m + n * n;
    if(tmp <= 1) {
      cout << "C" << endl;
    } else {
      bool prime = true;
      for(int i=0;i*i<=tmp;++i) {
        for(int j=0;i*i+j*j<=tmp;++j) {
          // cout << i << ":" << j << endl;
          GN gn2 = GN(i, j);
          if(gn2.isdivof(gn1) && !gn2.isprimedivof(gn1)) {
            prime = false;
          }
        }
      }
      cout << (prime ? "P" : "C") << endl;
    }

    --q;
  }
}

