}
set<pair<ll, ll> > ss;
bool isValid(ll k){
	for(ll m = 1; m * m < k; m++){
		ll n = sqrt(k - m * m);
		pair<ll, ll> pp = mp(m, n);
		if(n * n == k - m * m && ss.find(pp) == ss.end() && (m * p + n * q) % k == 0 && abs(m * q - n * p) % k == 0){
			//cout << m << " " << n << endl;
			return true;
		}
		pp = mp(m, -n); n = -n;
		if(n * n == k - m * m && ss.find(pp) == ss.end() && (m * p + n * q) % k == 0 && abs(m * q - n * p) % k == 0){
			//cout << m << " " << n << endl;
			return true;
		}
		n = -n;
		pp = mp(-m, -n); m = -m; n = -n;
		if(n * n == k - m * m && ss.find(pp) == ss.end() && (m * p + n * q) % k == 0 && abs(m * q - n * p) % k == 0){
			//cout << m << " " << n << endl;
			return true;
		}
		m = -m; n = -n;
		pp = mp(-m, n); m = -m;
		if(n * n == k - m * m && ss.find(pp) == ss.end() && (m * p + n * q) % k == 0 && abs(m * q - n * p) % k == 0){
			return true;
		}
		m = -m;
	}
	return false;
}
