	cin >> p >> q;
	tt = p * p + q * q;
	ss.clear(); 
	ss.insert(mp(0, 1)); ss.insert(mp(1, 0)); ss.insert(mp(0, -1)); ss.insert(mp(-1, 0));
	ss.insert(mp(p, q)); ss.insert(mp(-q, p)); ss.insert(mp(-p, -q)); ss.insert(mp(q, -p));
	if(tt <= 1){
		cout << "P" << endl;
		return;
	}
	bool flag = false;
	for(ll k1 = 2; k1 <= tt; k1++){
		ll k2 = tt / k1;
		if(k1 > 1 && k2 > 1 && tt % k1 == 0){
			if(isValid(k1) && isValid(k2)){
				cout << "C" << endl;
				return;
			}
		}
	}
	cout << "P" << endl;
}
