    FOR(i,0,n){
      ll a,b;
      cin>>a>>b;
      ll x = a*a + b*b;
      vector<ll> div = Divisors(x);
      char res = 'P';
      FOR(j,1,div.size()-1){
        ll p = div[j];
        ll q = x/p;
        if(mp[p] + mp[q] >= 2) res = 'C';
//        cout<<p<<" "<<mp[p]<<" & "<<q<<" "<<mp[q]<<endl;

      }
      cout<<res<<endl;
